an>			</div>		</div>		<div class="fright">			<a class="button green" style="margin-left:5px;" data-bind="add-order" title="Добавить заказ" href="<?=$path?>/add-order">Добавить заказ</a>		</div>	</div>		<table class="table-1" data-scroll="head">		<thead>			<tr>				<td class="small">№</td>				<td class="small">Дата</td>				<td class=""></td>				<td class="small">Сумма</td>				<td class="small">Скидка</td>				<td class="small">Итого</td>				<td class="small">Статус</td>				<td class="small"></td>			</tr>		</thead>		<tbody>			<?php $total = 0; $i=1; foreach($user->orders as $k):?>			<tr>				<td><?=$i++?></td>				<td><b><?=date('d-m-Y  H:i', $k->date)?></b></td>				<td class="left">					<a class="button blue" data-getorder-date="<?=date('d-m-Y  H:i', $k->date)?>" data-getorder="<?=$k->id?>" href="#get-order" title="просмотр товаров которые в этом заказе">просмотр заказа</a>				</td>				<td class="left nowrap"><?=number_format($k->sum, 2, ",", "'")?> грн.</td>				<td><?=$k->discount*1?>%</td>				<td class="left nowrap">					<?php $k->sum = $k->discount ? round($k->sum - $k->sum * $k->discount / 100, 2, PHP_ROUND_HALF_UP) : $k->sum;?> 					<?=number_format($k->sum, 2, ",", "'")?> грн.				</td>				<td class="left">					<select data-select="" data-order-status="<?=$k->id?>" style="display:none;">						<option value="0" <?=$k->status == 0 ? 'selected' : '';?>>ПРИНЯТ</option>						<option value="1" <?=$k->status == 1 ? 'selected' : '';?>>ВЫПОЛНЕНО</option>					</select>				</td>				<td>					<a title="удалить заказ" class="link_del" data-delete="заказ: <?=date('d-m-Y  H:i', $k->date)?>" href="<?=$path?>?delete=<?=$k->id?>"></a>				</td>			</tr>			<?php $total += $k->sum; endforeach;?>		</tbody>		<tfoot>			<tr>				<td class="right" colspan="5">					<br><b>Общая СУММА:</b><br><br>				</td>				<td class="nowrap">					<b><?=number_format($total, 2, ",", "'")?> грн.</b>				</td>				<td></td>			</tr>		</tfoot>	</table>	<?php endif;?>	</div><!-- END BODY --><script> // STATUS ORDER$('[data-order-status]').on( "selectmenuchange", function(e, ui){	var l = $('<div>').addClass('load');	$(document.body).append(l);		var id_order = $(this).attr('data-order-status');	var status = $(this).find(':selected').val();		$.post('', {set_status_order:'', id_order:id_order, status:status}, function(data){		l.remove();	});});</script><script> // ADD-ORDER$('[data-bind="add-order"]').on('click', function(e){	e.preventDefault();	$('<div class="">не работает</div>').dialog({		height:'auto',		width:600,		drag: true,		title:'Ошибка'	});});</script><script> // ORDERS (всплывающее окно с товарами заказа)$(document).on('click', '[data-getorder]', function(e){	e.preventDefault();		var order = $(this).attr('data-getorder'),		date = $(this).attr('data-getorder-date');		$.post('',{getorder:order}, function(data){		var html = '<table class="table-1">'+					'<thead>'+						'<tr>'+							'<td class="small">№</td>'+							'<td class="small"></td>'+							'<td>название</td>'+							'<td class="small">размер</td>'+							'<td class="small">опт/роз</td>'+							'<td class="small">цена</td>'+							'<td class="small">кол.</td>'+							'<td class="small">сумма</td>'+						'</tr>'+					'</thead>'+					'<tbody>';				$.each(data, function(i){			html += '<tr>'+						'<td>'+(i+1)+'</td>'+						'<td>'+							'<img width="50"  src="'+this.image+'" alt="">'+						'</td>'+						'<td class="left">'+							'<a href="'+this._url+'" target="_blank">'+this.pname+'</a>'+						'</td>'+						'<td class="nowrap">'+ (this.sizeName) +' <small>('+ (this.prefix) +')</small></td>'+						'<td class="nowrap">'+(+this.type ? 'опт.' : 'роз.')+'</td>'+						'<td class="nowrap">'+number_format(this.price, 2, ',', "'")+'</td>'+						'<td class="nowrap">'+(this.quantity)+'</td>'+						'<td class="nowrap">'+number_format(this.subtotal, 2, ',', "'")+'</td>'+					'</tr>';		});			html += '</tbody>'+			'</table>';				$(html).dialog({			height:'auto',			width:600,			drag: true,			title:'Информация о заказе '+date		});			}, 'json');	});</script>