ass="form-group">						<label>Отзыв:</label>						<textarea class="form-control-textarea" data-max-characters="500" name="comment"></textarea>						<div class="form-error"></div>					</div>										<div class="form-group">						<input type="hidden" name="add-review" value="">						<button class="btn btn-pink" type="submit" name="add-review">Добавить отзыв</button>					</div>				</div>			</div>		</div>	</form>	<?php else:?>			<h1 class="h1-style"><span><?=$h1?></span></h1>		<div class="row">		<div class="col-1">			<div style="margin:20px 0;">				<a class="btn btn-pink" href="/reviews/add-review">Добавить отзыв</a>			</div>			<?php				$res = array(					0 => array('grey',''),					1 => array('red','Очень плохо'),					2 => array('orange','Плохо'),					3 => array('yellow','Нормально'),					4 => array('pale-green','Хорошо'),					5 => array('green','Отлично')				);			?>			<?php foreach ($reviews as $review):?>			<?php $is = isset($res[$review->rating]) ? $res[$review->rating] : $res[0];?>			<div class="comment-box">				<div class="comment-question">					<div class="comment-info">						<div class="comment-avtor">							<?=$review->name ? $review->name : 'Пользователь скрыл свои данные';?>						</div>						<div class="comment-date"><?=rus_date_format($review->date)?></div>												<div class="comment-rating-box">							<div class="comment-rating">								<div class="comment-rating-indicator <?=$is[0]?>"></div>								<div class="comment-rating-substrate"></div>							</div>							<div class="comment-rating-text"><?=$is[1]?></div>						</div>					</div>					<div class="comment-body">						<div class="comment-body-layer"><?=$review->comment?></div>					</div>				</div>				<?php if (isset($review->answer_text)):?>				<div class="answere-box">					<div class="answere-body">						Cristalline:&nbsp;&nbsp;&nbsp;						<i><?=$review->answer_text?></i>					</div>				</div>				<?php endif;?>			</div>			<?php endforeac