<div class="body">		<h1 class="h1-style"><span><?=$h1?></span></h1>		<div class="row">		<div class="col-3"></div>		<div class="col-3">			<form action="" method="post">				<div class="form-group">					<label><b class="c-red">*</b> Ваше ФИО:</label>					<input class="form-control<?=isset($error['name'])?' error':'';?>" type="text" name="name" value="<?=isset($_POST['name']) ? htmlspecialchars($_POST['name']) : '';?>" placeholder="">					<div class="form-error"><?=isset($error['name']) ? $error['name'] : '';?></div>				</div>								<div class="form-group">					<label><b class="c-red">*</b> E-mail:</label>					<input class="form-control<?=isset($error['email'])?' error':'';?>" type="text" name="email" value="<?=isset($_POST['email']) ? htmlspecialchars($_POST['email']) : '';?>" placeholder="">					<div class="form-error"><?=isset($error['email']) ? $error['email'] : '';?></div>				</div>												<div class="form-group">					<label><b class="c-red">*</b> Моб.телефон:</label>					<input class="form-control<?=isset($error['phone'])?' error':'';?>" data-mask="phone" type="text" name="phone" value="<?=isset($_POST['phone']) ? htmlspecialchars($_POST['phone']) : '';?>" placeholder="">					<div class="form-error"><?=isset($error['phone']) ? $error['phone'] : '';?></div>				</div>								<div class="form-group">					<label>Город (Новая Почта):</label>					<select data-styler="" data-search="true" data-np="getWarenListNovaPoshta" name="city">							<option value="0" selected>&nbsp;</option>						<?php foreach ($cities['response'] as $city):?>						<option value="<?=$city->city_id?>"><?=$city->cityRu?></option>						<?php endforeach;?>					</select>				</div>								<div class="form-group">					<label>Отделение (Новая Почта):</label>					<select data-styler="" data-np="warenList" name="wareId">							<option value="0" selected>&nbsp;</option>					</select>				</div>												<div class="form-group">					<label><b class="c-red">*</b> Пароль:</label>					<input class="form-control<?=isset($error['pass'])?' error':'';?>" type="password" name="pass" value="<?=isset($_POST['pass']) ? htmlspecialchars($_POST['pass']) : '';?>" placeholder="" autocomplete="off">					<div class="form-error"><?=isset($error['pass']) ? $error['pass'] : '';?></div>				</div>								<div class="form-group">					<label><b class="c-red">*</b> Подтвердите пароль:</label>					<input class="form-control<?=isset($error['confirm_pass'])?' error':'';?>" type="password" name="confirm_pass" value="<?=isset($_POST['confirm_pass']) ? htmlspecialchars($_POST['confirm_pass']) : '';?>" placeholder="" autocomplete="off">					<div class="form-error"><?=isset($error['confirm_pass']) ? $error['confirm_pass'] : '';?></div>				</div>								<div class="form-group">					<label><b class="c-red">*</b> Введите символы указанные справа:</label>					<div class="row-cl">						<div style="float:left;width:50%;">							<input class="form-control<?=isset($error['captcha'])?' error':'';?>" data-bind="captcha" type="text" name="captcha" value="" autocomplete="off">							<div class="form-error"><?=isset($error['captcha'])? $error['captcha'] : '';?></div>						</div>						<div style="float:left;width:50%;">							<?=$captcha['image']?>						</div>					</div>				</div>								<div class="form-group right">					<button class="btn btn-white" name="add_user" value="">Регистрация</button>				</div>			</form>		</div>		<div class="col-3"></div>	</div></div><!-- END BODY --><script> // CAPS CAPTCHA$('[data-bind="captcha"]').bind('keydown.captcha', function v(e){	var k = e.keyCode,		w = String.fromCharCode(k),		s = $(this).val();	if (k == 8 || k == 37 || k == 39 || k == 46) return;		if (/[A-Z0-9]/gi.test(w)){		s += w;		$(this).val(s.substr(0,6).toUpperCase().replace(/[^A-Z0-9]/g, ''));	}		return false;}).bind('contextmenu', function(e){return false;});</script><script> // NOVA-POSHTA $('[data-np="getWarenListNovaPoshta"]').on("change", function(){	var l = $('<div>').addClass('load');	$(document.body).append(l);	$.post('/novaposhta',{getWarenList:$(this).val()},function(data){		var warenList = $('select[data-np="warenList"]').html('<option value="0"> </option>');		$.each(data.response, function(){			warenList.append('<option value="'+ this.wareId +'">'+ this.addressRu +'</option>');		});		warenList.trigger('refresh');		l.remove();	}, 'json');	});</script>		