<div class="body">	<h1 class="h1-style"><span><?=$h1?></span></h1>		<!--############################################################### CRUMBS-->	<ul class="crumbs">		<li itemtype="http://data-vocabulary.org/Breadcrumb" itemscope="">			<a itemprop="url" href="<?=SERVER?>">				<span itemprop="title">Главная</span>			</a>		</li>		<li> / </li>		<?php $last = array_pop($crumbs);?>		<?php foreach ($crumbs as $k):?>		<li  itemtype="http://data-vocabulary.org/Breadcrumb" itemscope="">			<a itemprop="url" href="<?=$k['_url']?>">				<span itemprop="title"><?=$k['name']?></span>			</a>		</li>		<li> / </li>		<?php endforeach;?>		<li><?=$last['name']?></li>	</ul>	<!--############################################################### END CRUMBS-->		<div class="row">		<div class="col-3"></div>		<div class="col-3">			<form action="" method="post" id="passport" data-bind="edit-user">				<div class="form-group">					<label><b class="c-red">*</b> Ваше ФИО (для заполнения документов):</label>					<input class="form-control<?=isset($error['name'])?' error':'';?>" type="text" name="name" value="<?=$user->name;?>" placeholder="">					<div class="form-error"><?=isset($error['name']) ? $error['name'] : '';?></div>				</div>									<div class="form-group">					<label><b class="c-red">*</b> E-mail:</label>					<input class="form-control<?=isset($error['email'])?' error':'';?>" type="text" name="email" value="<?=$user->email;?>" placeholder="">					<div class="form-error"><?=isset($error['email']) ? $error['email'] : '';?></div>				</div>												<div class="form-group">					<label><b class="c-red">*</b> Моб.телефон:</label>					<input class="form-control<?=isset($error['phone'])?' error':'';?>" data-mask="phone" type="text" name="phone" value="<?=$user->phone;?>" placeholder="">					<div class="form-error"><?=isset($error['phone']) ? $error['phone'] : '';?></div>				</div>												<div class="form-group">					<label>Город (Новая Почта):</label>					<select data-styler="" data-search="true" data-np="getWarenListNovaPoshta" name="city">							<option value="0" selected>&nbsp;</option>						<?php foreach ($cities['response'] as $city):?>						<option value="<?=$city->city_id?>" <?=$user->city == $city->city_id ? ' selected' : '';?>><?=$city->cityRu?></option>						<?php endforeach;?>					</select>				</div>								<div class="form-group">					<label>Отделение (Новая Почта):</label>					<select data-styler="" data-np="warenList" name="wareId">							<option value="0" selected>&nbsp;</option>						<?php foreach ($warenList['response'] as $k):?>						<option value="<?=$k->wareId?>" <?=$user->wareId == $k->wareId ? ' selected' : '';?>><?=$k->addressRu?></option>						<?php endforeach;?>					</select>				</div>								<div style="margin:20px 0 0 0;">					<input type="hidden" name="token" value="<?=$user->token?>">					<input type="hidden" name="edit_user" value="">					<button class="btn btn-white" type="submit" name="edit_user" value="<?=$user->id;?>">Применить изменения</button>				</div>			</form>		</div>		<div class="col-3"></div>	</div></div><!-- END BODY --><script> // NOVA-POSHTA $('[data-np="getWarenListNovaPoshta"]').on("change", function(){	var l = $('<div>').addClass('load');	$(document.body).append(l);	$.post('/novaposhta',{getWarenList:$(this).val()},function(data){		var warenList = $('select[data-np="warenList"]').html('<option value="0"> </option>');		$.each(data.response, function(){			warenList.append('<option value="'+ this.wareId +'">'+ this.addressRu +'</option>');		});		warenList.trigger('refresh');		l.remove();	}, 'json');	});</script>